// --- GLOBAL CONFIGURATION (MINDHAFEN) ---

// 1. Nichos (Salud Mental & Bienestar)
const niches = [
  "Psicólogos", 
  "Terapeutas Holísticos", 
  "Centros de Yoga", 
  "Clínicas de Estrés", 
  "Coach de Vida", 
  "Retiros Espirituales",
  "Psiquiatras",
  "Centros de Meditación"
];

// 2. Ubicaciones (SIN ARGENTINA)
const locations = [
  "Ciudad de México, México",
  "Monterrey, México",
  "Guadalajara, México",
  "Bogotá, Colombia",
  "Medellín, Colombia",
  "Santiago, Chile",
  "Lima, Perú",
  "Madrid, España",
  "Barcelona, España",
  "Valencia, España",
  "Miami, FL, USA"
];

// 3. Selección Random
const niche = niches[Math.floor(Math.random() * niches.length)];
const location = locations[Math.floor(Math.random() * locations.length)];

return {
  search_term: niche,
  location: location,
  // Banner MindHafen (sube tu banner a assets/images/mindhafen_banner.jpg y hostealo)
  banner_url: "https://images.unsplash.com/photo-1544367563-12123d895951?q=80&w=800&auto=format&fit=crop"
};

// Obtener links ya guardados (con manejo de errores)
let existingRaw = [];
try {
    const sheetData = $('Leer Leads Existentes').all();
    if (sheetData && sheetData.length > 0) {
        existingRaw = sheetData.map(x => x.json);
    }
} catch(e) {
    // Si falla (primera ejecución o sheet vacío), continuar sin duplicados
    existingRaw = [];
}

// Extraer dominios simples de la hoja para comparar
const existingDomains = existingRaw.map(x => {
    const url = x.Website || x.Web || x.Link || '';
    try { 
        if (url) return new URL(url).hostname.replace('www.', ''); 
    } catch(e) { 
        return url; 
    }
    return '';
}).filter(d => d); // Filtrar vacíos

// Item actual viene de 'Dividir' con campo 'website'
const currentUrl = $input.item.json.website || '';
let currentDomain = '';
try {
    if (currentUrl) currentDomain = new URL(currentUrl).hostname.replace('www.', '');
} catch(e) { 
    currentDomain = currentUrl; 
}

// Filtrar si ya existe
if (currentDomain && existingDomains.length > 0 && existingDomains.some(d => d && d.includes(currentDomain))) {
    return []; // Descartar duplicado
}

return $input.item;

const html = $input.item.json.data || '';
const emailRegex = /([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/gi;
const mailtoRegex = /mailto:([^\?"'>]+)/gi;
let foundEmails = [];
let match;
while ((match = mailtoRegex.exec(html)) !== null) {
    if (match[1]) foundEmails.push(match[1]);
}
const textMatches = html.match(emailRegex) || [];
foundEmails = [...foundEmails, ...textMatches];
const uniqueEmails = [...new Set(foundEmails.map(e => e.trim().toLowerCase()))];
const validEmails = uniqueEmails.filter(e => !e.match(/\.(png|jpg|jpeg|gif|css|js|svg)$/i) && e.length < 100 && e.includes('@'));
const bestEmail = validEmails.length > 0 ? validEmails[0] : '';
const allEmails = validEmails.join(', ');
const text = html.replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '').replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '').replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ');
const title = $('Loop').item.json.title || 'Sin nombre';
const phone = $('Loop').item.json.phone || '';
return {
  text: text.substring(0, 5000),
  title: title,
  phone_from_maps: phone,
  regex_email: bestEmail,
  all_emails: allEmails
};

let data = $input.item.json;

// --- INTENTO DE RECUPERAR JSON SI LA IA ALUCINÓ ---
if (!data.email && !data.phone) {
    const possibleString = data.text || data.output || data.response || '';
    if (typeof possibleString === 'string') {
        try {
            // Buscar el último bloque JSON completo si hay texto basura antes
            const first = possibleString.indexOf('{');
            const last = possibleString.lastIndexOf('}');
            if (first >= 0 && last > first) {
                const jsonStr = possibleString.substring(first, last + 1);
                const parsed = JSON.parse(jsonStr);
                data = { ...data, ...parsed };
            }
        } catch (e) { 
             // Fallback regex simple
             try {
                 const match = possibleString.match(/\{[\s\S]*\}/);
                 if (match) {
                     const parsed = JSON.parse(match[0]);
                     data = { ...data, ...parsed };
                 }
             } catch(e2) {}
        }
    }
}

const title = $('Loop').item.json.title || 'Sin nombre';
const phone_maps = $('Loop').item.json.phone || '';
const banner = $('CONFIG GLOBAL').item.json.banner_url;
const regex_email_input = $('Limpiar HTML').item.json.regex_email;

// Datos con fallback (SIN NUMERO DUMMY)
const rawPhone = data.phone || phone_maps || '';
const phone = rawPhone.replace(/[+\s-]/g, '');

// PRIORITY: AI Email > Regex Email > Fallback
let email = (data.email && data.email.trim() !== '' && data.email !== '""') ? data.email : regex_email_input;
if (!email || email === '' || email === '""' || email === 'SIN-EMAIL@nodetectado.local') email = 'SIN-EMAIL@nodetectado.local';

const score = data.score || 5;

// MindHafen Asset URL
const guideUrl = 'https://mindhafen.generarise.space/guia-descompresion.html';

// Contenido del email (viene de IA o fallback)
const emailBody = data.email_body || '<p>Hola equipo de <strong>' + title + '</strong>,</p><p>En MindHafen hemos desarrollado una herramienta para ayudar a pacientes con ansiedad mediante técnicas de descompresión neuronal.</p><p>Nos encantaría compartirla con ustedes.</p>';

// Template HTML MindHafen
const email_html_mindhafen = '<!DOCTYPE html>' +
'<html lang="es">' +
'<head>' +
'    <meta charset="UTF-8">' +
'    <meta name="viewport" content="width=device-width, initial-scale=1.0">' +
'    <title>MindHafen - Recursos para Profesionales</title>' +
'</head>' +
'<body style="margin: 0; padding: 0; font-family: \'Segoe UI\', Arial, sans-serif; background-color: #f4f4f7; color: #333;">' +
'    <table width="100%" cellpadding="0" cellspacing="0" border="0" style="background-color: #f4f4f7; padding: 40px 20px;">' +
'        <tr>' +
'            <td align="center">' +
'                <table width="600" cellpadding="0" cellspacing="0" border="0" style="background-color: #ffffff; border-radius: 12px; overflow: hidden; box-shadow: 0 4px 20px rgba(0,0,0,0.05); border: 1px solid #f0f0f0;">' +
'                    <tr>' +
'                        <td style="background-image: url(\'' + banner + '\'); background-size: cover; background-position: center; height: 180px; position: relative;">' +
'                            <div style="position: absolute; top:0; left:0; width:100%; height:100%; background: linear-gradient(180deg, rgba(100,50,150,0.3) 0%, rgba(50,30,100,0.7) 100%);"></div>' +
'                            <div style="position: absolute; bottom: 20px; left: 30px; color: #ffffff; font-size: 28px; font-weight: bold; text-shadow: 0 0 15px rgba(0,0,0,0.9), 0 0 25px rgba(100,50,150,0.8), 2px 2px 6px rgba(0,0,0,1); letter-spacing: 1.5px;">' +
'                                MINDHAFEN' +
'                            </div>' +
'                        </td>' +
'                    </tr>' +
'                    <tr>' +
'                        <td style="padding: 40px 30px; color: #1a202c; line-height: 1.6; font-size: 16px; background-color: #ffffff;">' +
                            emailBody +
'                            <div style="text-align: center; margin-top: 30px;">' +
'                                <a href="' + guideUrl + '" style="display: inline-block; background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: #ffffff; padding: 12px 30px; text-decoration: none; border-radius: 50px; font-weight: 600; margin-top: 20px; box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);">' +
'                                    DESCARGAR GUÍA GRATUITA' +
'                                </a>' +
'                            </div>' +
'                        </td>' +
'                    </tr>' +
'                    <tr>' +
'                        <td style="background-color: #f9fafb; padding: 20px; text-align: center; border-top: 1px solid #eee; font-size: 12px; color: #888;">' +
'                            <p style="margin-bottom: 5px;"><strong>MindHafen - Recursos Clínicos</strong></p>' +
'                            <p style="margin: 0;">Neurociencia Aplicada • <a href="https://mindhafen.generarise.space/dashboard.html" style="color:#667eea; text-decoration:none;">Plataforma Profesional</a></p>' +
'                        </td>' +
'                    </tr>' +
'                </table>' +
'            </td>' +
'        </tr>' +
'    </table>' +
'</body>' +
'</html>';

// Clean WA
let waMsg = data.wa_msg || 'Hola equipo de ' + title + ', somos MindHafen. Tenemos un recurso gratuito para manejo de ansiedad en pacientes. ¿Les gustaría recibirlo?';
waMsg = waMsg.replace(/^(Mensaje|Message|WA|WhatsApp)(:|\s)+/i, '').replace(/^[\"']|[\"']$/g, '').trim();

return {
  text: title,
  score: score,
  email_address: email,
  phone: phone,
  wa_msg: waMsg,
  email_html: email_html_mindhafen,
  search_term: $('CONFIG GLOBAL').item.json.search_term,
  location: $('CONFIG GLOBAL').item.json.location,
  raw_ai: JSON.stringify(data)
};
